function constructAddressSpace(server, addressSpace, eventObjects, done) {
    // Creamos constantes para algunos objetos y as simplificar la escritura
    const miopcua       = coreServer.core.nodeOPCUA; 				        // Acceso a nodeopcua
    const LocalizedText = coreServer.core.nodeOPCUA.LocalizedText; 	// Para simplificar textos localizados
    const namespace     = addressSpace.getOwnNamespace();			    // Espacio de nombres
    const Variant       = miopcua.Variant;
    const DataType      = miopcua.DataType;


    // Este mensaje sale en el panel de debug
    node.warn('Espacio de direcciones gateway PI 1')

    // Uso de una variable local para mantener un valor sobre el que leer/escribir
    var fake=0.0
     //----------------------------------------------------------------------
    let A0 = false;
    let enabled = true;
    //----------------------------------------------------------------------

    // Creacion de objeto para el alimentador de bases
    let bases = namespace.addObject({
        browseName  : 'Alimentador Bases',
        description : 'Estacion numero 1 de la planta ',
        organizedBy : addressSpace.rootFolder.objects,
        notifierOf  : addressSpace.rootFolder.objects.server 
    })

    let Ix_A0 = namespace.addVariable({
        propertyOf : bases,  // Variable solo de lectura
        nodeID     : 'ns=1;s=IxA0',
        browseName : 'Sensor Retroceso',

         displayName: [
          new LocalizedText({text: 'Forward', locale: 'en-US'}),
          new LocalizedText({text: 'Alante', locale: 'es-ES'})
        ],
        description: 'Sensor del vastago del cilindro',
        dataType: DataType.Boolean,
        value:
        {
            get : function(){
                return new miopcua.Variant({
                    dataType : DataType.Boolean,
                    value:global
                })
            }

        }

    })


    // Creamos un objeto para la Raspberry Pi, nuestro gateway IoT
    // https://node-opcua.github.io/tutorial/2015/12/05/ObjectType-and-object-instantiation.html
    //----------------------------------------------------------------------

    let rpi = namespace.addObject({
        browseName: 'RPi Gateway',
        description: 'Objeto que representa una RPi haciendo de gateway IoT',
        organizedBy: addressSpace.rootFolder.objects,
        notifierOf: addressSpace.rootFolder.objects.server
    })


    //----------------------------------------------------------------------
    // AÃ±adimos variables al objeto. Hay dos modos, como propertyOf o como
    // componentOf. La segunda funciona para Leer/Escribir desde un cliente
    // la primera solo me ha funcionado como solo lectura.
    //----------------------------------------------------------------------

    let piTempCPU = namespace.addVariable({
        propertyOf: rpi,		// Propiedad del objeto (solo lectura)
        nodeID: 'ns=1;s=piTemp',
        browseName: 'Temp CPU',

        displayName: [
          new LocalizedText({text: 'RPi CPU temp', locale: 'en-US'}),
          new LocalizedText({text: 'Temperatura CPU de la Pi', locale: 'es-ES'})
        ],
        description: 'Temperatura de la CPU de la RPi',
        dataType: 'Double',
        eventSourceOf: rpi,
        value:
        {   
            get: function () {
                return new miopcua.Variant({
                dataType: 'Double',
                value:global.rpiTemp
              })
            },
         }

    })

    // Vamos a aÃ±adir otra variable para la temperatura de la CPU
    // de la Pi... pero esta vez como una variable analÃ³gica con
    // informaciÃ³n de rango y unidades de ingeniera
    // Para aÃ±adir variables analÃ³gicas con rangos ver
    // http://node-opcua.github.io/api_doc/0.1.0/classes/AddressSpace.html
    let piTempCPU2 = namespace.addAnalogDataItem({
        componentOf: rpi,	// Componente (tag) del objeto, puede ser L/E
        nodeID: 'ns=1;s=piTemp2',
        browseName: 'Temp CPU2',

        engineeringUnitsRange: {
            low:  0,
            high: 100.0
        },
        engineeringUnits: miopcua.standardUnits.degree_celsius,
	    // Ver unidades en https://github.com/node-opcua/node-opcua/blob/7ed0ff7a/packages/node-opcua-data-access/source/EUInformation.ts#L87

        displayName: [
          new LocalizedText({text: 'RPi CPU temp 2', locale: 'en-US'}),
          new LocalizedText({text: 'Temperatura CPU de la Pi 2', locale: 'es-ES'})
        ],
        description: 'Temperatura de la CPU de la RPi 2',
        dataType: 'Double',
        eventSourceOf: rpi,
        value:
        {   
            get: function () {
              return new miopcua.Variant({
                dataType: 'Double',
                value:fake
              })
            },
            set: function (variant) {
                    fake = parseFloat(variant.value)
                    return miopcua.StatusCodes.Good
            }
        }

    })

     // Indicamos que es una variable que historiza
    addressSpace.installHistoricalDataNode(piTempCPU2);

    // Vamos a aÃ±adir una Ãºltima variable, pero esta vez que devuelva el 
    // valor de una funciÃ³n interna. Es una variable de solo lectura
    // que podrmos calcular a partir de otros datos. AdemÃ¡s
    // vamos a historizarla. La llamaremos PresiÃ³n.

    let t=0; // contador
    const simPresion = namespace.addAnalogDataItem({
        browseName: "Presion",
        //nodeID: 'ns=1;s=Presion',
        engineeringUnitsRange: {
            low:  0,
            high: 10.0
        },
        engineeringUnits: miopcua.standardUnits.bar,
        componentOf: rpi,
        value:{
            get: function(){
                let valuep = (Math.sin(t/50)*0.70+Math.random()*0.20)*5.0+5.0;
                t=t+1; 
                return new miopcua.Variant({
                    dataType: miopcua.DataType.Double,
                    value:valuep
                  })
            }
        }
    });

    // Indicamos que es una variable que historiza
    addressSpace.installHistoricalDataNode(simPresion);


    //----------------------------------------------------------------------
    // AÃ±adimos ahora un mÃ©todo al objeto rpi. Primero se aÃ±aden los datos
    // del mÃ©todo al namespace: nombre, argumentos con tipo y descripciÃ³n,
    // etc. Luego se hace un bind sobre una funciÃ³n javascript
    //----------------------------------------------------------------------

    // AÃ±adimos el mÃ©todo
    const m1=namespace.addMethod(rpi,{
        browseName: "CalcSen",
        nodeID:"ns=1;s=CalcSen",
        inputArguments:[{
            name: "Angulo (radianes)",
            description: {text: "Angulo en radianes para el seno"},
            dataType: 'Double'
        }],
        outputArguments:[{
            name: "Seno",
            description: {text: "Valor del seno"},
            dataType: 'Double'
        }]
    })

    // Y ahora lo atamos a una funciÃ³n
    m1.bindMethod(function(inputArguments,context,callback) {
        const angulo = inputArguments[0].value; // Valor del argumento
	    // Creamos un objeto para el resultado
        const callMethodResult = {
            statusCode: miopcua.StatusCodes.Good,
            outputArguments: [{
                    dataType: 'Double',
                    value : Math.sin(angulo)
            }]
        };
	// Se devuelve el resultado as:
        callback(null,callMethodResult);
    });


   //----------------------------------------------------------------------
   // Una vez terminado el script, hay que llamar a done() para instalarlo
   //----------------------------------------------------------------------
   done()
}

